CREATE TABLE `comments` (
  `id` int(11) NOT NULL,
  `author` varchar(60) NOT NULL,
  `nickname` varchar(60) NOT NULL,
  `pubdate` datetime NOT NULL DEFAULT current_timestamp(),
  `text` text NOT NULL,
